import React, {useState, useEffect} from 'react';
import './App.css';
import BlogPosts from "./BlogPosts";
import {PostDTO} from "./api/models/PostDTO";
import BlogPostForm from "./BlogPostForm";

function App() {
    const [blogPosts, setBlogPosts] = useState<PostDTO[]>([]);
    function fetchBlogPosts() {
        fetch("http://localhost:3000/post")
          .then(response => response.json())
          .then(posts => setBlogPosts(posts))
          .catch(error => console.log(error));
      }
    useEffect(()=> {
        fetchBlogPosts();
     });
    const yourName = "Bence";
    const onFormSubmit = (post: PostDTO) => {
        fetch("http://localhost:3000/post", {
            method: "POST",
            headers: {
                "Content-Type": "application/json"
            },
            body: JSON.stringify(post)
        })
            .then(() => fetchBlogPosts())
            .catch(error => console.log(error));
     }


    return (
        <div className="App">
            <header className="App-header">
                {yourName + "'s Personal Travel Blog"}
            </header>
            <body className="App-body">
            <BlogPostForm onPostSubmit={onFormSubmit}/>
            <BlogPosts blogPosts={blogPosts}/>
            </body>
        </div>
    );
}

export default App;
