import React, {useState, useEffect} from 'react';
import './App.css';
import BlogPosts from "./BlogPosts";
import {PostDTO} from "./api/models/PostDTO";
import BlogPostForm from './BlogPostForm';
import BlogCard from './BlogCard';
function App() {
    const [blogPosts, setBlogPosts] = useState<PostDTO[]>([]);
    async function fetchBlogPosts() {
        try {
            const response = await fetch("http://localhost:3002/posts");
            const data = await response.json();
            return setBlogPosts(data);
        } catch (error) {
            return console.log(error);
        }
    }
    useEffect(() => {
        fetchBlogPosts();
    }, [setBlogPosts]);
   // Please fill this with your own blog posts. ðŸ™‚
   const onFormSubmit = (post: PostDTO) => {
    fetch("http://localhost:3002/post", {
        method: "POST",
        headers: {
            "Content-Type": "application/json"
        },
        body: JSON.stringify(post)
    })
        .then(() => fetchBlogPosts())
        .catch(error => console.log(error));
 }
   return (
       <div className="App">
           <header className="App-header">
               {"[Your Name Here]'s personal blog."}
           </header>

       </div>
   );
}


export default App;
