import React, {useState} from 'react';
import {Button, Form} from "react-bootstrap";
import { PostDTO } from './api/models/PostDTO';

const crypto = require('crypto');

type Props = {
onPostSubmit: (blogPost: PostDTO) => void;
};

function BlogPostForm(props: Props) {
const [author, setAuthor] = useState("nobody");
const [title, setTitle] = useState("");
const [text, setText] = useState("");
const handleSubmit = (event: React.FormEvent<HTMLFormElement>) => {
    event.preventDefault();

    if (!form.checkValidity()) {
        return;
    }

    const blogPost = new PostDTO();
    blogPost.id = crypto.randomUUID();
    blogPost.author = author;
    blogPost.title = title;
    blogPost.text = text;

    props.onPostSubmit(blogPost);
};

return (
    <Form className="mb-3" style={{width: "60vw"}} onSubmit={handleSubmit}>
        <Form.Group className="mb-3" controlId="formTitle">
            <Form.Label>Title</Form.Label>
            <Form.Control required type="text" placeholder="Enter the title" size="lg" value={title} onChange={e => setTitle(e.target.value)} />
        </Form.Group>
        <Form.Group className="mb-3" controlId="formText">
            <Form.Label>Text</Form.Label>
            <Form.Control required as="textarea" rows={3}/>
        </Form.Group>
        <Form.Group className={"mb-3"} controlId="formAuthor">
            <Form.Label>Author</Form.Label>
            <Form.Control type="text" placeholder="Enter the author name"/>
        </Form.Group>
        <Button variant="primary" type="submit">Save</Button>
    </Form>
);}

export default BlogPostForm;
