import React, { useState, useEffect } from 'react';
import './App.css';
import BlogPosts from './BlogPosts';
import { PostDTO } from './api/models/PostDTO';
import BlogPostForm from './BlogPostForm';
import { Container, Row, Col, Spinner, Alert } from 'react-bootstrap';

function App() {
  const [blogPosts, setBlogPosts] = useState<PostDTO[]>([]);
  const [isLoading, setIsLoading] = useState<boolean>(true);
  const [error, setError] = useState<string | null>(null);

  const fetchBlogPosts = () => {
    setIsLoading(true);
    fetch('http://localhost:3000/post')
      .then((response) => response.json())
      .then((data) => {
        setBlogPosts(data.posts); // assuming the array is stored in a property called posts
        setIsLoading(false);
      })
      .catch((error) => {
        setError(error.message);
        setIsLoading(false);
      });
  };
  useEffect(() => {
    fetchBlogPosts();
  }, []);

  const yourName = 'Bence';
  const onFormSubmit = (post: PostDTO) => {
    fetch('http://localhost:3000/post', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(post),
    })
      .then(() => fetchBlogPosts())
      .catch((error) => console.log(error));
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>{yourName + "'s Personal Travel Blog"}</h1>
      </header>
      <body className="App-body">
        <Container className="my-3">
          <Row>
            <Col lg={{ span: 6, offset: 3 }}>
              <BlogPostForm onPostSubmit={onFormSubmit} />
              {isLoading ? (
                <div className="text-center py-3">
                  <Spinner animation="border" />
                </div>
              ) : error ? (
                <Alert variant="danger">{error}</Alert>
              ) : (
                <BlogPosts blogPosts={blogPosts} />
              )}
            </Col>
          </Row>
        </Container>
      </body>
    </div>
  );
}

export default App;
